jb.ns('facebook')

jb.component('facebook.main', { /* facebook.main */
  type: 'control',
  impl: group({
    controls: [
      group({
        controls: [
          group({
            style: layout.flex({alignItems: 'center', spacing: '50'}),
            controls: [
              picklist({
                title: '',
                databind: '%$sortBy/by%',
                options: picklist.options(list('number-of-likes', 'postLength', 'date')),
                features: [css.width('150'), field.onChange(refreshControlById('posts-list'), true)]
              }),
              editableBoolean({
                databind: '%$sortBy/sort%',
                style: editableBoolean.mdlSlideToggle(),
                textForTrue: 'ascending',
                textForFalse: 'descending',
                features: watchRef({ref: '%$sortBy/sort%', allowSelfRefresh: false})
              }),
              button({
                title: 'Get posts of training',
                action: writeValueAsynch(
                  '%$table%',
                  pipe(
                    '%$TrainingUrls%',
                    http.get({vars: [Var('url', '%%')], url: '%%'}),
                    pipeline(facebook.postParser())
                  )
                )
              }),
              button({
                title: 'Hide/Show all images',
                action: runActions(runActionOnItems('%$table%', toggleBooleanValue('%isShown%')))
              })
            ]
          }),
          itemlist({
            items: pipeline(
              '%$table%',
              sort({
                  propertyName: '%$sortBy/by%',
                  lexical: 'false',
                  ascending: '%$sortBy/sort%'
                })
            ),
            controls: [
              group({
                style: card.card('1200', '16'),
                controls: [
                  label('תאריך %printedDate% '),
                  label({
                    title: '%number-of-likes% לייקים',
                    features: css.padding({left: '5', right: ''})
                  }),
                  html({title: 'html', html: '%post-text%', features: css('text-align: right')}),
                  label({title: 'אורך %postLength%', features: css('direction : ltr')}),
                  label({title: '%url% קישור', features: css('direction : ltr')}),
                  image({url: '%img%', features: hidden('%isShown%')}),
                  button({
                    title: data.if('%isShown%', 'hide image', 'show image'),
                    action: toggleBooleanValue('%isShown%'),
                    features: watchRef({ref: '%isShown%', allowSelfRefresh: 'yes'})
                  })
                ],
                features: css.margin({bottom: '10'})
              })
            ],
            style: table.withHeaders(),
            features: [
              id('posts-list'),
              watchRef({ref: '%$table%', includeChildren: 'yes', allowSelfRefresh: true}),
              watchRef({ref: '%$sortBy%', includeChildren: 'yes', allowSelfRefresh: true})
            ]
          })
        ],
        features: css('direction: rtl')
      })
    ]
  })
})

jb.component('facebook.fb-page-parser', { /* facebook.fbPageParser */
  type: 'data',
  impl: pipeline(
    extractText({
        startMarkers: ['class=\"_1whp _4vn2\"', 'class=\"_3hg- _42ft\"', 'href=\"'],
        endMarker: '\"',
        repeating: 'true'
      }),
    replace('amp;')
  ),
  testData: '%$train%'
})

jb.component('facebook.post-parser', { /* facebook.postParser */
  type: 'data',
  impl: pipeline(
    Var(
        'urlFromHtml',
        pipeline(
          extractText({
              startMarkers: list('link rel=\"alternate\"', 'href=\"'),
              endMarker: '\"'
            }),
          replace('amp;'),
          replace({find: 'm.facebook.com', replace: 'www.facebook.com'})
        )
      ),
    obj(
        prop(
            'post-text',
            extractText({
              startMarkers: ['userContent', '<p>'],
              endMarker: '<div class=\"_3x-2\"'
            })
          ),
        prop(
            'username',
            extractText({
              startMarkers: ['class=\"fwn fcg\"', 'class=\"fwb fcg\"', 'href=\"https://www.facebook.com', '>'],
              endMarker: '<'
            })
          ),
        prop(
            'number-of-likes1',
            pipeline(
              extractText({
                  startMarkers: ['url:\"https://www.facebook.com', 'i18n_reaction_count:\"'],
                  endMarker: '\"',
                  repeating: 'true'
                }),
              replace({find: 'K', replace: '*1000'}),
              ({data}) => eval(data),
              aggregate(ctx => Math.max.apply(0, ctx.data))
            )
          ),
        prop(
            'number-of-likes2',
            pipeline(
              extractText({startMarkers: 'class=\"_81hb\">', endMarker: '<'}),
              replace({find: 'K', replace: '*1000'}),
              ({data}) => eval(data)
            )
          ),
        prop('date', extractText({startMarkers: 'data-utime=\"', endMarker: '\"'})),
        prop(
            'url',
            pipeline(
              extractText({
                  startMarkers: list('link rel=\"alternate\"', 'href=\"'),
                  endMarker: '\"'
                }),
              replace('amp;')
            )
          ),
        prop(
            'img',
            pipeline(
              extractText({startMarkers: 'data-ploi=\"', endMarker: '\"'}),
              replace('amp;')
            )
          ),
        prop('isShown', 'false', 'boolean'),
        prop(
            'likes',
            extractText({
              startMarkers: ['%$urlFromHtml%\",i18n_reaction_count:\"'],
              endMarker: '\"'
            })
          )
      ),
    extend(
        prop(
            'number-of-likes',
            ({data}) => data['number-of-likes2'] === '' ? data['number-of-likes1'] : data['number-of-likes2'],
            'number'
          )
      ),
    extend(
        prop('postLength', firstSucceeding('%post-text/length%', '1'), 'number'),
        prop(
            'printedDate',
            pipeline('%date%', evalExpression('new Date(%%000).toDateString()'))
          )
      )
  ),
  testData: '%$SampleTrainingPost%'
})

